mapper.select:
SELECT * FROM community_comments
WHERE comment_id = #{commentId}
AND community_id = #{communityId}

SELECT * FROM community_comments
WHERE parent_comment_id = #{commentId}
AND is_deleted = FALSE

mapper.update:
UPDATE community_comments
SET content = #{content}, updated_at = NOW()
WHERE comment_id = #{commentId}

mapper.delete:
UPDATE community_comments
SET is_deleted = TRUE
WHERE comment_id = #{commentId}
