mapper.select:
SELECT ic.category_name
FROM info_categories ic
WHERE ic.category_id = #{category_id}

SELECT u.nickname
FROM users u
WHERE u.user_id = #{user_id}

SELECT COUNT(user_id)
FROM info_likes
WHERE info_id = #{infoId}

mapper.update:
UPDATE info
SET view_count = view_count + 1
WHERE info_id = #{infoId}
