mapper.select:

SELECT i.*
FROM info i
JOIN info_categories c ON i.category_id = c.category_id



    c.category_name = #{infoData}

AND is_deleted = 0


ORDER BY

    ${order.property} ${order.direction}



LIMIT #{pageable.pageSize} OFFSET #{pageable.offset}

SELECT COUNT(*) FROM info i JOIN info_categories c ON i.category_id = c.category_id
c.category_name = #{infoData}
AND is_deleted = 0

SELECT ic.category_name
FROM info_categories ic
WHERE ic.category_id = #{category_id}

SELECT u.nickname
FROM users u
WHERE u.user_id = #{user_id}

SELECT COUNT(user_id)
FROM info_likes
WHERE info_id = #{infoId}

mapper.update:
UPDATE info
SET category_id = #{categoryId}, title = #{title}, content = #{content}, image_url = #{imageUrl}, updated_at =
#{updatedAt}
WHERE info_id = #{infoId}

UPDATE info
SET is_deleted = 1
WHERE info_id = #{infoId}

UPDATE info
SET view_count = view_count + 1
WHERE info_id = #{infoId}
