mapper.select:
SELECT community_id AS id, title, view_count AS views, (SELECT COUNT(*) FROM community_comments WHERE community_comments.community_id = community.community_id AND community_comments.is_deleted = FALSE) AS comments, created_at AS date FROM community WHERE user_id = #{userId} AND is_deleted = FALSE
AND title LIKE CONCAT('%', #{search}, '%')
ORDER BY created_at DESC LIMIT #{pageable.pageSize} OFFSET #{pageable.offset}

SELECT routine_id AS id, title, view_count AS views, (SELECT COUNT(*) FROM routine_comments WHERE routine_comments.routine_id = routines.routine_id AND routine_comments.is_deleted = FALSE) AS comments, created_at AS date FROM routines WHERE user_id = #{userId} AND is_deleted = FALSE
AND title LIKE CONCAT('%', #{search}, '%')
ORDER BY created_at DESC LIMIT #{pageable.pageSize} OFFSET #{pageable.offset}
